#!/bin/bash

# RPG Maker MZ character sprite format: 288x256 (6 cols x 4 rows, each sprite 48x64)
# Base64 de sprite básico de personaje

CHARACTER_SPRITE=""

# Crear sprites de personaje
echo "$CHARACTER_SPRITE" | base64 -d > Player.png
echo "$CHARACTER_SPRITE" | base64 -d > Oak.png
echo "$CHARACTER_SPRITE" | base64 -d > Rival.png
echo "$CHARACTER_SPRITE" | base64 -d > NPC01.png
echo "$CHARACTER_SPRITE" | base64 -d > NPC02.png
echo "$CHARACTER_SPRITE" | base64 -d > NPC03.png

# Sprite transparente 1x1 para characters vacíos
TRANSPARENT="iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNk+M9QDwADhgGAWjR9awAAAABJRU5ErkJggg=="
echo "$TRANSPARENT" | base64 -d > !Flame.png
echo "$TRANSPARENT" | base64 -d > !Other1.png
echo "$TRANSPARENT" | base64 -d > !Other2.png

echo "✅ Created character sprite files"
ls -lh *.png

