#!/bin/bash

# Crear sprites placeholder para enemigos (Pokemon)
# 128x128 image base

ENEMY_SPRITE=""

# Crear algunos sprites de Pokemon placeholder con diferentes nombres
# Los nombres deberían coincidir con los que usa Enemies.json
echo "$ENEMY_SPRITE" | base64 -d > Pokemon_001.png
echo "$ENEMY_SPRITE" | base64 -d > Pokemon_002.png
echo "$ENEMY_SPRITE" | base64 -d > Pokemon_003.png
echo "$ENEMY_SPRITE" | base64 -d > Pokemon_004.png
echo "$ENEMY_SPRITE" | base64 -d > Pokemon_005.png

# Placeholder genérico
echo "$ENEMY_SPRITE" | base64 -d > Enemy.png

echo "✅ Created enemy sprite files"
ls -lh *.png

