#!/bin/bash

# PNG mínimo 1x1 transparente (en base64)
TRANSPARENT_1X1="iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNk+M9QDwADhgGAWjR9awAAAABJRU5ErkJggg=="

# Crear imágenes básicas necesarias
for file in Balloon.png Shadow1.png Shadow2.png Damage.png States.png ButtonSet.png Loading.png; do
    echo "$TRANSPARENT_1X1" | base64 -d > "$file"
    echo "✓ Created $file"
done

# Window.png (192x192 con borde)
echo "" | base64 -d > Window.png
echo "✓ Created Window.png (192x192)"

# IconSet.png (512x512 grid de iconos)
echo "" | base64 -d > IconSet.png
echo "✓ Created IconSet.png (512x512)"

# Weapons
for i in 1 2 3; do
    echo "$TRANSPARENT_1X1" | base64 -d > "Weapons${i}.png"
    echo "✓ Created Weapons${i}.png"
done

echo "✅ Imágenes del sistema creadas!"
